mapper.select:
select e.*,e1.name,r.rname
from employee e join empstate e1 ON e.stateid=e1.id
join role r on e.roleid=r.rid


    and (e.enumber like concat('%',#{ename},'%')
    or e.ename like concat('%',#{ename},'%'))


    and e.stateid = #{id}


    and e.eusername=#{eusername}


    and e.epassword=#{epassword}

 limit #{start},#{pageSize}

SELECT count(*) FROM employee e join empstate e1 ON e.stateid=e1.id
and (enumber like concat('%',#{ename},'%') or e1.name like concat('%',#{ename},'%'))
and e.stateid = #{id}
